mapper.update:
UPDATE board
SET bo_hit = bo_hit + 1
WHERE bo_no = #{boNo}

UPDATE board
SET bo_cd = #{boCd}, bo_title = #{boTitle}, bo_content = #{boContent}, bo_mod_date = sysdate
WHERE bo_no = #{boNo}

INSERT INTO board ( 
		  bo_no
       , bo_cd
       , mem_id
       , bo_title
       , bo_content
       , bo_reg_date
       , bo_mod_date
       , bo_hit
		) VALUES (
		BOARD_SEQ.nextval
		, #{boCd}
		, #{memId}
		, #{boTitle}
		, #{boContent}
		, sysdate
		, null
		, 0
		)

mapper.insert:
INSERT INTO board ( 
